local_file = "res/coypu.jpg"


def test_initialize():


    # from Thread message list

    th = Thread([Msg(Msg.Kind.IN, "Hello model!"),
                Msg(Msg.Kind.OUT, "Hello there human!")],
                inst="Be helpful.")
    
    assert th.as_dict() == {'_msgs': [{'kind': 'IN', 'text': 'Hello model!', 'images': None},
        {'kind': 'OUT', 'text': 'Hello there human!', 'images': None}],
        'inst': {'kind': 'INST', 'text': 'Be helpful.', 'images': None},
        'join_sep': '\n'}
    

    th = Thread(["Hello model!",
                "Hello there human!"],
                inst="Be helpful.")
    
    assert th.as_dict() == {'_msgs': [{'kind': 'IN', 'text': 'Hello model!', 'images': None},
        {'kind': 'OUT', 'text': 'Hello there human!', 'images': None}],
        'inst': {'kind': 'INST', 'text': 'Be helpful.', 'images': None},
        'join_sep': '\n'}


    th = Thread([
                Msg(Msg.Kind.IN, "Hello model! Describe this image?", [{"url": "https://images.net/squirrel.jpeg", "detail": "high" ,"other": "bla-bla"}]),
                Msg(Msg.Kind.OUT, "Hello there human! It's a squirrel."),
                Msg(Msg.Kind.IN, "What about these?", [{"url": "data:image/jpeg;base64,abcdef01234567890"}, 
                                                        {"url": "https://images.net/boat78.jpeg"}]),
                Msg(Msg.Kind.OUT, "Boats, all boats"),
                ],
                
                inst="Be helpful.")

    assert th.as_dict() == {'_msgs': [{'kind': 'IN',
        'text': 'Hello model! Describe this image?',
        'images': [{'url': 'https://images.net/squirrel.jpeg',
            'detail': 'high',
            'other': 'bla-bla'}]},
        {'kind': 'OUT', 'text': "Hello there human! It's a squirrel.", 'images': None},
        {'kind': 'IN',
        'text': 'What about these?',
        'images': [{'url': 'data:image/jpeg;base64,abcdef01234567890'},
            {'url': 'https://images.net/boat78.jpeg'}]},
        {'kind': 'OUT', 'text': 'Boats, all boats', 'images': None}],
        'inst': {'kind': 'INST', 'text': 'Be helpful.', 'images': None},
        'join_sep': '\n'}





    th = Thread([
             Msg(Msg.Kind.IN, "Hello model! Describe this image?", [{"url": local_file}]),
             "Is it a banana?",
            ],
            
            inst="Be helpful.")

    assert th.as_dict() == {'_msgs': [{'kind': 'IN',
        'text': 'Hello model! Describe this image?',
        'images': [{'url': 'data:image/jpeg;base64,'}]},
        {'kind': 'OUT', 'text': 'Is it a banana?', 'images': None}],
        'inst': {'kind': 'INST', 'text': 'Be helpful.', 'images': None},
        'join_sep': '\n'}
    


    # from ChatML message list

    th = Thread([
             {"role": "user", "content":"Hello model!"},
             {"role": "assistant", "content":"Howdy?"},
            ],
            
            inst="Be helpful.")    
    
    assert th.as_dict() == {'_msgs': [{'kind': 'IN', 'text': 'Hello model!', 'images': None},
        {'kind': 'OUT', 'text': 'Howdy?', 'images': None}],
        'inst': {'kind': 'INST', 'text': 'Be helpful.', 'images': None},
        'join_sep': '\n'}



