def test_add():

    th = Thread()
    th.add(Msg.Kind.IN, "In 1")
    th.add(Msg.Kind.IN, "In 2")
    th.add(Msg.Kind.OUT, "Out 1")
    th.add(Msg.Kind.INST, "Do inst 1")
    th.add(Msg.Kind.INST, "Do inst 2")

    assert th.as_dict() == {'_msgs': [{'kind': 'IN', 'text': 'In 1\nIn 2', 'images': None},
        {'kind': 'OUT', 'text': 'Out 1', 'images': None}],
        'inst': {'kind': 'INST', 'text': 'Do inst 1\nDo inst 2', 'images': None},
        'join_sep': '\n'}



    th = Thread()
    th.inst.text = "Do this don't do that."

    th.add(Msg.Kind.INST, "Also answer in rhymes.")

    th.add(Msg.Kind.IN, "In 1")
    th.add(Msg.Kind.IN, "In 1 line 2") # joined to last message

    th.add(Msg.Kind.OUT, "OUT 1")

    th.add(Msg.Kind.IN, "In 2")

    th.add(Msg.Kind.OUT, "Out 2")
    
    assert th.as_dict() == {'_msgs': [{'kind': 'IN', 'text': 'In 1\nIn 1 line 2', 'images': None},
        {'kind': 'OUT', 'text': 'OUT 1', 'images': None},
        {'kind': 'IN', 'text': 'In 2', 'images': None},
        {'kind': 'OUT', 'text': 'Out 2', 'images': None}],
        'inst': {'kind': 'INST',
        'text': "Do this don't do that.\nAlso answer in rhymes.",
        'images': None},
        'join_sep': '\n'}




def test_add_IN_OUT():

    th = Thread()

    th.init_INST_IN("Be persuasive yet kind.",
                    "How to smile and be thruthful?")
    th.add_OUT("Just be yourself, if possible...")

    assert th.as_dict() == {'_msgs': [{'kind': 'IN',
        'text': 'How to smile and be thruthful?',
        'images': None},
        {'kind': 'OUT', 'text': 'Just be yourself, if possible...', 'images': None}],
        'inst': {'kind': 'INST', 'text': 'Be persuasive yet kind.', 'images': None},
        'join_sep': '\n'}
    



    th = Thread()

    th.init_INST_IN("Be persuasive yet kind.",
                    "How to smile and be thruthful?",
                    ["https://images.net/squirrel.jpeg", 
                    {"url": "https://images.net/squirrel2.jpeg", "detail": "low"},]
                )

    th.add_IN("Honest answer, please?",
            "https://images.net/squirrel3.jpeg")

    th.add_IN("Real, really, really honest answer?",
            {"url": "https://images.net/squirrel4.jpeg"})

    th.add_IN("I'm begging.",
            {"url": "data:image/jpeg;base64,abcdef01234567890"})

    th.add_IN("Real, really, really honest answer?",
            local_file)

    th.add_IN("?",
            {"url": local_file, "detail": "low"})


    assert th.as_dict() == {'_msgs': [{'kind': 'IN',
        'text': "How to smile and be thruthful?\nHonest answer, please?\nReal, really, really honest answer?\nI'm begging.\nReal, really, really honest answer?\n?",
        'images': [{'url': 'https://images.net/squirrel.jpeg'},
            {'url': 'https://images.net/squirrel2.jpeg', 'detail': 'low'},
            {'url': 'https://images.net/squirrel3.jpeg'},
            {'url': 'https://images.net/squirrel4.jpeg'},
            {'url': 'data:image/jpeg;base64,abcdef01234567890'},
            {'url': 'data:image/jpeg;base64,'},
            {'url': 'data:image/jpeg;base64,',
            'detail': 'low'}]}],
        'inst': {'kind': 'INST', 'text': 'Be persuasive yet kind.', 'images': None},
        'join_sep': '\n'}


    th = Thread()
    th.inst.text = "Do this don't do that."
    th.add(Msg.Kind.INST, "Also answer in rhymes.")

    th.add("In 1")
    th.add(Msg.Kind.IN, "In 1 line 2") # joined to last message

    th.add("OUT 1")

    th.add({"role": "user", "content": [{"type": "text", "text": "Hello model! What's that?"},
                                        {"type": "image_url", "image_url": {"url": 'https://images.net/squirrel.jpeg', "detail": "low"}}]})
        
    th.add({"role": "assistant", "content":"Easy: a squirrel."})

    assert th.as_dict() == {'_msgs': [{'kind': 'IN', 'text': 'In 1\nIn 1 line 2', 'images': None},
        {'kind': 'OUT', 'text': 'OUT 1', 'images': None},
        {'kind': 'IN',
        'text': "Hello model! What's that?",
        'images': [{'url': 'https://images.net/squirrel.jpeg', 'detail': 'low'}]},
        {'kind': 'OUT', 'text': 'Easy: a squirrel.', 'images': None}],
        'inst': {'kind': 'INST',
        'text': "Do this don't do that.\nAlso answer in rhymes.",
        'images': None},
        'join_sep': '\n'}







def test_plus_equal():

    th1 = Thread(["Hello model!",
                "Hello there human!"],
                inst="Be helpful.")    

    th2 = Thread(["2Hello model!",
                "2Hello there human!"],
                inst="Be helpful.")    

    th1 += th2

    assert th1.as_dict() == {'_msgs': [{'kind': 'IN', 'text': 'Hello model!', 'images': None},
        {'kind': 'OUT', 'text': 'Hello there human!', 'images': None},
        {'kind': 'IN', 'text': '2Hello model!', 'images': None},
        {'kind': 'OUT', 'text': '2Hello there human!', 'images': None}],
        'inst': {'kind': 'INST', 'text': 'Be helpful.\nBe helpful.', 'images': None},
        'join_sep': '\n'}




    th = Thread(["Hello model!",
                "Hello there human!"],
                inst="Be helpful.")    

    th += "What should I do today?"
    th += "You should take a walk."

    assert th.as_dict() == {'_msgs': [{'kind': 'IN', 'text': 'Hello model!', 'images': None},
        {'kind': 'OUT', 'text': 'Hello there human!', 'images': None},
        {'kind': 'IN', 'text': 'What should I do today?', 'images': None},
        {'kind': 'OUT', 'text': 'You should take a walk.', 'images': None}],
        'inst': {'kind': 'INST', 'text': 'Be helpful.', 'images': None},
        'join_sep': '\n'}
    



    th = Thread(["Hello model!",
                "Hello there human!"],
                inst="Be helpful.")    

    th += ["What should I do today?", "You should take a walk."]

    assert th.as_dict() == {'_msgs': [{'kind': 'IN', 'text': 'Hello model!', 'images': None},
        {'kind': 'OUT', 'text': 'Hello there human!', 'images': None},
        {'kind': 'IN', 'text': 'What should I do today?', 'images': None},
        {'kind': 'OUT', 'text': 'You should take a walk.', 'images': None}],
        'inst': {'kind': 'INST', 'text': 'Be helpful.', 'images': None},
        'join_sep': '\n'}




    th = Thread(["Hello model!"],
                inst="Be helpful.")    

    th += [Msg(Msg.Kind.IN, "2Hello model!"),
            Msg(Msg.Kind.OUT, "2Hello there human!")]

    assert th.as_dict() == {'_msgs': [{'kind': 'IN', 'text': 'Hello model!\n2Hello model!', 'images': None},
        {'kind': 'OUT', 'text': '2Hello there human!', 'images': None}],
        'inst': {'kind': 'INST', 'text': 'Be helpful.', 'images': None},
        'join_sep': '\n'}
    



    th = Thread(["Hello model!",
                "Hello there human!"],
                inst="Be helpful.")    

    th += [{"role": "user", "content":"2Hello model!"},
        {"role": "assistant", "content":"2Howdy?"},
        ]

    assert th.as_dict() == {'_msgs': [{'kind': 'IN', 'text': 'Hello model!', 'images': None},
        {'kind': 'OUT', 'text': 'Hello there human!', 'images': None},
        {'kind': 'IN', 'text': '2Hello model!', 'images': None},
        {'kind': 'OUT', 'text': '2Howdy?', 'images': None}],
        'inst': {'kind': 'INST', 'text': 'Be helpful.', 'images': None},
        'join_sep': '\n'}
    

def test_equality():

    th = Thread(["Hello model!",
                "Hello there human!"],
                inst="Be helpful.")    

    th += [{"role": "user", "content":"2Hello model!"},
        {"role": "assistant", "content":"2Howdy?"},
        ]
    assert th[0] == Msg(Msg.Kind.IN, "Hello model!") and th[-1] == Msg(Msg.Kind.OUT, "2Howdy?")
    


    th1 = Thread(["Hello model!",
                "Howdy?"],
                inst="Be helpful.")    

    th2 = Thread([{"role": "user", "content":"Hello model!"},
                {"role": "assistant", "content":"Howdy?"},],
                inst="Be helpful.")    
    assert th1 == th2



    th1 = Thread([
                Msg(Msg.Kind.IN, "Hello model! Describe this image?", [{"url": "https://images.net/squirrel.jpeg", "detail": "high" ,"other": "bla-bla"}]),
                Msg(Msg.Kind.OUT, "Hello there human! It's a squirrel."),
                ],
                inst="Be helpful.")

    th2 = Thread([
                Msg(Msg.Kind.IN, "Hello model! Describe this image?", [{"url": "https://images.net/squirrel.jpeg", "detail": "high" ,"other": "bla-bla"}]),
                "Hello there human! It's a squirrel.",
                ],
                inst="Be helpful.")
    
    assert th1 == th2    



    th1 = Thread([
                Msg(Msg.Kind.IN, "Hello model! Describe this image?", [{"url": "https://images.net/squirrel.jpeg", "detail": "high" ,"other": "bla-bla"}]),
                Msg(Msg.Kind.OUT, "Hello there human! It's a squirrel."),
                ],
                inst="Be helpful.")

    th2 = Thread([
                Msg(Msg.Kind.IN, "Hello model! Describe this image?", [{"url": "https://images.net/squirrel.jpeg", "detail": "high" ,"other": "bla-bla"}]),
                "Hello there human! It's a squirrel.",
                ],
                inst="Be helpful.")

    assert th1.as_chatml() == th2.as_chatml()


    th = Thread(["Hello model!",
                "Hello there human!"],
                inst="Be helpful.")    

    th += ["What should I do today?", "You should take a walk."]

    th += "That's quite umpleasant"
    th += "See if I care"

    th += {"role": "user", "content": [{"type": "text", "text": "I can't believe your bad manners!!! What's that?"},
                                    {"type": "image_url", "image_url": {"url": 'https://images.net/donkey.jpeg', "detail": "low"}}]}
    th += {"role": "assistant", "content":"That's a donkey for sure."}

    assert th.as_dict() == {'_msgs': [{'kind': 'IN', 'text': 'Hello model!', 'images': None},
        {'kind': 'OUT', 'text': 'Hello there human!', 'images': None},
        {'kind': 'IN', 'text': 'What should I do today?', 'images': None},
        {'kind': 'OUT', 'text': 'You should take a walk.', 'images': None},
        {'kind': 'IN', 'text': "That's quite umpleasant", 'images': None},
        {'kind': 'OUT', 'text': 'See if I care', 'images': None},
        {'kind': 'IN',
        'text': "I can't believe your bad manners!!! What's that?",
        'images': [{'url': 'https://images.net/donkey.jpeg', 'detail': 'low'}]},
        {'kind': 'OUT', 'text': "That's a donkey for sure.", 'images': None}],
        'inst': {'kind': 'INST', 'text': 'Be helpful.', 'images': None},
        'join_sep': '\n'}







def test_trim():

    th = Thread([Msg.make_IN("Hello model!"),
                Msg.make_OUT("Hello there human!"),
                "Things are great aren't they?",
                "If you so say...",
                Msg.make_IN("Describe this image?", [{"url": "https://images.net/squirrel.jpeg", "detail": "high" ,"other": "bla-bla"}]),
                Msg.make_OUT("Yeah, it's a squirrel."),
                ],
                
                inst="Be helpful.")

    def thread_token_len(thread: Thread) -> int:
        total = len(thread.inst.text)
        for msg in thread:
            total += len(msg.text)
            if msg.images:
                total += len(str(msg.images))
        return total

    trim_flags = Thread.Trim.INST | Thread.Trim.KEEP_FIRST_IN | Thread.Trim.IN | Thread.Trim.OUT    


    th.trim(trim_flags,
            180,
            thread_token_len)

    assert th.as_dict() == {'_msgs': [{'kind': 'IN',
        'text': "Hello model!\nThings are great aren't they?\nDescribe this image?",
        'images': [{'url': 'https://images.net/squirrel.jpeg',
            'detail': 'high',
            'other': 'bla-bla'}]},
        {'kind': 'OUT', 'text': "Yeah, it's a squirrel.", 'images': None}],
        'inst': {'kind': 'INST', 'text': '', 'images': None},
        'join_sep': '\n'}






def test_misc():

    th = Thread(["Hello model!",
                "Hello there human!"],
                inst="Be helpful.")    

    th += [{"role": "user", "content":"2Hello model!"},
        {"role": "assistant", "content":"2Howdy?"},
        ]
    assert th[1:3] == [th[1],th[2]]

    th = Thread([
             Msg(Msg.Kind.IN, "Hello model! Describe this image?", [{"url": local_file}]),
             "Is it a banana?",
            ],
            
            inst="Be helpful.")

    assert th.has_images == True

    # iterator with INST message
    assert len(list(th.get_iter(True))) == len(list(th.get_iter(False))) + 1
